import React, { useState,useEffect} from 'react'
import"../UI/UserView.css"
import Component from './Component'
let components=[{
  "id":0,
  "value":null
},
{
  "id":1,
  "value":null
},
{
  "id":2,
  "value":null
},
{
  "id":3,
  "value":null
},
{
  "id":4,
  "value":null
},
{
  "id":5,
  "value":null
},
{
  "id":6,
  "value":null
},
{
  "id":7,
  "value":null
},
{
  "id":8,
  "value":null
}
]
function UserView() {
    const [count,setCount]=useState(components)
    const [turn,setTurn]=useState("X")
   const displayCount=(id)=>{
     if(count[id].value===null)
     {
       if(turn==="X"){

        setTurn(0)
        count[id].value=0
        setCount([...count])
       }else{
        count[id].value=turn
        setCount([...count])
        }
     }
   }     
  return (
      <div className="parent">
          <div className='heding'><h1>Tik-Tac-Toe</h1></div>
    <div className='main-comp'>
      {
          count.map((index)=>{
            return <Component key={index.id} count={index} displayCount={displayCount }/>
          })

      }
    </div>
    </div>
  )
}

export default UserView
